mapper.select:
select d.*, c.name AS category_name from dish d left outer join category c on d.category_id = c.id



        and d.name like concat('%',#{name},'%')


        and d.category_id = #{categoryId}


        and d.status = #{status}


order by create_time desc

mapper.update:
update dish
name = #{name}, price = #{price}, image = #{image}, category_id = #{categoryId}, description = #{description}, create_user = #{createUser}, create_time = #{createTime}, update_time = #{updateTime}, update_user = #{updateUser}, status = #{status},
where id = #{id}

mapper.select:
select * from dish


        and name like concat('%',#{name},'%')


        and category_id = #{categoryId}


        and status = #{status}


order by create_time desc
